mapper.select:
select date,sum(pv) as pv,sum(uv) as uv , sum(uip) as uip from t_link_access_stats las

    inner join t_link l on las.full_short_url = l.full_short_url and las.gid = l.gid and l.del_flag = 0

where las.full_short_url = #{fullShortUrl} and las.gid = #{gid} and las.date between #{startDate} and #{endDate} 
group by las.date;

select date,sum(pv) as pv,sum(uv) as uv , sum(uip) as uip from t_link_access_stats las

    inner join t_link l on las.full_short_url = l.full_short_url and las.gid = l.gid and l.del_flag = 0

where las.gid = #{gid} and las.date between #{startDate} and #{endDate} group by las.date;
